void addin(int l,int r,int k,int b){
	A[l]+=b; B[l]+=k;
	A[r+1]-=b; B[r+1]-=k;
}
void add(int l,int r,int where,int w){
	if (l>r) return;
	int wherel=(l+where-1)%n+1,wherer=(r+where-1)%n+1;
	int wl=abs(wherel-w),wr=abs(wherer-w);
	if (wl<=wr) addin(l,r,wl-l,1);
