	if (x == y) return;
	if (DSUsize[x] < DSUsize[y]) swap(x, y);
	DSUsize[x] += DSUsize[y]; DSUpar[y] = x;
	for (auto it=neighbors[y].begin(); it!=neighbors[y].end(); it++) {
