["#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define vll vector<ll>
#define vvll vector<vll>

#define pll pair<ll,ll>

struct SEG {
    vector<pll> arr;
    vector<bool> tswap;

    void make(ll n) {
        arr.resize(4*n);
        tswap.resize(4*n);
    }

    void pull(ll v) {
        arr[v] = arr[v*2];
        arr[v].first ^= arr[v*2+1].first;
        arr[v].second ^= arr[v*2+1].second;
    }

    void dswap(ll v) {
        tswap[v] = !tswap[v];
        swap(arr[v].first, arr[v].second);
    }

    void push(ll v) {
        if (tswap[v]) {
            dswap(v*2);
            dswap(v*2+1);
            tswap[v] = 0;
        }
    }

    void set(ll v, ll tl, ll tr, ll ind, pll val) {
        if (tl == tr) {
            arr[v] = val;
            return;
        }
        ll tm = (tl + tr) / 2;
        if (ind <= tm)
            set(v*2, tl, tm, ind, val);
        else set(v*2+1, tm+1, tr, ind, val);
        pull(v);
    }

    void rswap(ll v, ll tl, ll tr, ll l, ll r) {
        if (l > r) return;
        if (tl == l && tr == r) {
            dswap(v);
            return;
        }
        push(v);
        ll tm = (tl + tr) / 2;
        rswap(v*2, tl, tm, l, min(r, tm));
        rswap(v*2+1, tm+1, tr, max(l,tm+1), r);
        pull(v);
    }
};

int main() {
    ios_base::sync_with_stdio(false); cin.tie(0); cout.tie(0);
    int t;
    cin >> t;
    while (t--) {
        ll n;
