long double inter(int i, int j) {
  long double vv = value[i];
  vv *= value[j];
  long double fvv = vv * (f[j] - f[i]);
  long double gv = ((long double)g[i] * value[j]) - ((long double)g[j] * value[i]);
  long double vmv = value[j] - value[i];
  return (fvv + gv) / vmv;
}

