  int m = 1;
  for (int i = 1; i < n; i++) {
    int profit = -h[i].second;
    int last_p = -h[m - 1].second;
    if (profit > last_p) {
      h[m] = h[i];
      m++;
    }
  }
  n = m;
  for (int i = 0; i < n; i++) {
    cost[i] = h[i].first;
    value[i] = -h[i].second;
  }
  int e = 0;
  for (int i = 0; i < n; i++) {
    f[i] = inf;
    g[i] = 0;
    if (cost[i] == 0) {
      f[i] = 0;
      g[i] = 0;
    } else {
      int ll = 1, rr = e;
      while (ll < rr) {
        int mid = (ll + rr) >> 1;
        if (xs[mid] < cost[i]) ll = mid + 1;
