inline void update(node * v)
{
	if (v==NULL)
		return;
	v->roz=1;
	v->war+=v->narz;
	if (v->lew!=NULL)
	{
		v->lew->narz+=v->narz;
		v->roz+=v->lew->roz;
	}
	if (v->pra!=NULL)
	{
		v->pra->narz+=v->narz;
		v->roz+=v->pra->roz;
	}
	v->narz=0;
	//tutaj dodajemy wszystkie operacje zwizane z funkcjami m.in. drzewa przedziaowego, tzn. lazy propagation itp.
}
node * merge(node * v, node * u)
{
	if (v==NULL)
		return u;
	if (u==NULL)
		return v;
	if ((v->prior)>=(u->prior))
	{
		update(v);//czasem mona usun
		v->pra=merge(v->pra, u);
		if (v->pra!=NULL)
			v->pra->oj=v;
		update(v);
		return v;
	}
	else
	{
		update(u);//czasem mona usun
		u->lew=merge(v, u->lew);
		if (u->lew!=NULL)
