	line() {}
	line(int X1, int Y1, int X2, int Y2) {
		if (X1 < X2)	
			x1 = X1, x2 = X2, y1 = Y1, y2 = Y2; else
			x1 = X2, x2 = X1, y1 = Y2, Y2 = y1;
	}
	inline int k() const {
		return x1==x2 ? 1 : (y2-y1) / (x2-x1);
	}
	inline int at(int x) const {
		return y1 + (x-x1) * k();
	}
} l1[N], l2[N];
int n1, n2, l[N];
inline bool conj(const line &A, const line &B) {
	const line &a = A.x1 > B.x1 ? B : A, &b = A.x1 > B.x1 ? A : B;
	if (a.x2 < b.x1) return 0;
	return a.at(b.x1) == b.y1;
}
inline bool inter(const line &a, const line &b) {
	int x = -2 * (b.at(0) - a.at(0)) / (b.k() - a.k());
	return a.x1*2<=x && a.x2*2>=x && b.x1*2<=x && b.x2*2>=x;
}
line merge(const line &A, const line &B) {
	const line &a = A.x1 > B.x1 ? B : A, &b = A.x1 > B.x1 ? A : B;
