}

const int sz = 1 << 18;
struct segtree {
    pii seg[sz << 1];
    segtree() {
        for (int i = 0; i < (sz << 1); ++i) seg[i] = pii(inf, inf);
    }
    void init() {
        for (int i = sz; --i; ) seg[i] = seg[i << 1] + seg[i << 1 | 1];
    }
    void update(int x, int v) {
        x += sz;
        seg[x] = pii(v, inf);
        while (x >>= 1)
            seg[x] = seg[x << 1] + seg[x << 1 | 1];
    }
    pii query(int x, int y) {
        x += sz;
        y += sz;
        pii ret = pii(inf, inf);
        while (x <= y) {
            if ((x & 1) == 1) ret = ret + seg[x++];
            if ((y & 1) == 0) ret = ret + seg[y--];
            x >>= 1;
            y >>= 1;
        }
        return ret;
    }
} seg[10];

