	if (cur == 0) {
		remin(lowest[v], h[dest]);
	}
	if (h[dest] > lowest[v]) {
		return;
	}
	if (cur >= could[v]) {
		return;
	}
	could[v] = cur;
	for (int x : same[v]) {
		dfs(x, cur + 1, dest);
	}
	for (int x : up[v]) {
		dfs(x, cur ? cur - 1 : 0, dest);
	}
}

