using namespace std;
typedef long long LL;

// SA from http://www.prefield.com/algorithm/string/suffix_array.html

struct SaComp {
	const int sp, *sr, srlen;
	SaComp(int sp, const vector<int>& sr) : sp(sp), sr(&sr[0]), srlen(sr.size()) {}
	bool operator()(int a, int b) const
	  { return make_pair(sr[a], a+sp<srlen?sr[a+sp]:0x7fffffff)
	         < make_pair(sr[b], b+sp<srlen?sr[b+sp]:0x7fffffff); }
};

template<typename RanIt>
vector<int> compute_suffix_array(RanIt beg, RanIt end)
{
	const int N = end - beg;

	vector<int> sa(N);
	vector<int> sort_rank(beg, end);
	for(int i=0; i<N; ++i)
		sa[i] = i;

	sort(sa.begin(), sa.end(), SaComp(0, sort_rank));
	for(int sorted_prefix=1; sorted_prefix<N; sorted_prefix*=2)
	{
		SaComp cmp(sorted_prefix, sort_rank);
		sort(sa.begin(), sa.end(), cmp);

		vector<int> block_id(N);
		for(int i=1; i<N; ++i)
			block_id[i] = block_id[i-1] + (cmp(sa[i-1], sa[i]) ? 1 : 0);
		for(int i=0; i<N; ++i)
			sort_rank[sa[i]] = block_id[i];
	}
	return sa;
}

vector<int> inv_sa(const vector<int>& sa)
{
	vector<int> isa(sa.size());
	for(int i=0; i<sa.size(); ++i)
		isa[sa[i]] = i;
	return isa;
}

template<typename RanIte>
vector<int> longest_common_prefix(RanIte beg, RanIte end, const vector<int>& sa)
{
	const int N = sa.size();
	vector<int> lcp(N);
	vector<int> inv = inv_sa(sa);

	int len = 0;
	for(int i=0; i<N; ++i) {
		int sa_idx = inv[i];
		if( sa_idx == 0 )
			lcp[sa_idx] = -1;
		else {
			for(int k=sa[sa_idx-1]; i+len<N && k+len<N && *(beg+i+len)==*(beg+k+len);)
				++len;
			lcp[sa_idx] = len;
		}
		if(len) --len;
	}
	return lcp;
}

template<typename T>
struct RMQ
{
	vector< vector<int> > rm;
	vector<T> d;

	RMQ( const vector<T>& d ) : d(d) {
		int n = d.size();

		// rm[k][x] = i s.t. d[i] is the minimum in [x, x+2^k)
		rm.push_back( vector<int>(n) );
		for(int x=0; x<n; ++x)
			rm[0][x] = x;
		for(int k=1; (1<<k)<=n; ++k) {
			rm.push_back( rm[k-1] );
			for(int x=0; x+(1<<k-1)<n; ++x)
				if( d[rm[k][x]] > d[rm[k-1][x + (1<<k-1)]] )
					rm[k][x] = rm[k-1][x + (1<<k-1)];
		}
	}

	// min {i in [L,R] | d[i] is minumum among d[L..R]}
	int operator()(int L, int R) const {
		int k=0;
		for(; L+(1<<k) < R-(1<<k)+1; ++k) {}
		int i = rm[k][L];
		int j = rm[k][R-(1<<k)+1];
		return (d[i]<=d[j] ? i : j);
	}

	// {i in [L,R] | d[i] is minumum among d[L..R]}
	vector<int> all(int L, int R) const {
		vector<int> ans;
		int minValue = d[(*this)(L, R)];
		while( L <= R ) {
			int C = (*this)(L, R);
			if( minValue < d[C] )
				break;
			ans.push_back(C);
			L = C+1;
		}
		return ans;
	}

	// max {i in [L,R] | d[i]<X}, or -1
	int rightmost_less_than_X(int L, int R, T X) const {
		if(L>R) return -1;

		int k=0;
		for(; L+(1<<k) < R-(1<<k)+1; ++k) {}

		int i = rm[k][L];
		int j = rm[k][R-(1<<k)+1];
		if( !(d[i]<X || d[j]<X) )
			return -1;
		if( d[j] < X )
			L = R-(1<<k)+1;

		for(; k; --k) { // Answer is in [L, L+(1<<k))
			i = rm[k-1][L];
			j = rm[k-1][L+(1<<k-1)];
			if( d[j] < X )
				L += 1<<k-1;
		}
		return L;
	}

	// min {i in [L,R] | d[i]<X}, or -1
	int leftmost_less_than_X(int L, int R, T X) const {
		if(L>R) return -1;

		int k=0;
		for(; L+(1<<k) < R-(1<<k)+1; ++k) {}

		int i = rm[k][L];
		int j = rm[k][R-(1<<k)+1];
		if( !(d[i]<X || d[j]<X) )
			return -1;
		if( !(d[i] < X) )
			L = R-(1<<k)+1;

		for(; k; --k) { // Answer is in [L, L+(1<<k))
			i = rm[k-1][L];
			j = rm[k-1][L+(1<<k-1)];
			if( !(d[i] < X) )
				L += 1<<k-1;
		}
		return L;
	}
};

void solve(const vector<string>& SS, int K)
{
	string S;
	vector<int> sep;
	for(int i=0; i<SS.size(); ++i) {
		if(i) S += '$';
		S += SS[i];
		sep.push_back(S.size());
	}

	int sa_end = S.size();
	vector<int> sa = compute_suffix_array(S.begin(), S.end());
	vector<int> lcp = longest_common_prefix(S.begin(), S.end(), sa);
	int sa_start = 0;
	while(S[sa[sa_start]]<'A')
		++sa_start;

	vector<int> blk_of(sa_end, -1);
	for(int i=sa_start; i<sa_end; ++i) {
		int blk = lower_bound(sep.begin(), sep.end(), sa[i]) - sep.begin();
		blk_of[i] = blk;
	}

	vector<int> first_index_of_K_diff_blocks(sa_end, 0x7fffffff);
	map<int,int> blk_set;
	for(int i=sa_start,k=sa_start; i<sa_end; ++i) {
		for(; blk_set.size()<K && k<sa_end; ++k)
			blk_set[blk_of[k]]++;
		if( blk_set.size() == K )
			first_index_of_K_diff_blocks[i] = k-1;
		if( --blk_set[blk_of[i]] == 0 )
			blk_set.erase(blk_of[i]);
	}



	RMQ<int> rmq( vector<int>(lcp.begin(), lcp.end()) );

	vector<LL> total(SS.size());
	for(int s=sa_start; s<sa_end; ++s)
	{
		int len_L=1, len_R=sep[blk_of[s]] - sa[s];
		int possible_len = 0;
		while( len_L <= len_R )
		{
			int len_C = (len_L+len_R) / 2;

			int l = rmq.rightmost_less_than_X(sa_start, s, len_C);
			if(l==-1) l=sa_start;
			int r = rmq.leftmost_less_than_X(s+1, sa_end-1, len_C);
			if(r==-1) r=sa_end-1; else r--;

			// [l,r] has same prefix of length >= len_C as s
			if( first_index_of_K_diff_blocks[l] <= r ) {
				possible_len = len_C;
				len_L = len_C + 1;
			} else
				len_R = len_C - 1;
		}
		total[blk_of[s]] += possible_len;
	}

	for(int i=0; i<total.size(); ++i)
		cout << (i?" ":"") << total[i];
	cout<<endl;
}

int main()
{
	for(int N,K; cin>>N>>K; )
	{
		vector<string> SS(N);
		for(int i=0; i<SS.size(); ++i)
			cin >> SS[i];
		solve(SS, K);
	}
}
