struct N{
	N*to[4];
	int cnt=0,mn[4][4],mx[4][4];
	N(){
		rep(i,4)to[i]=0;
		rep(i,4)rep(j,4)mn[i][j]=inf;
		rep(i,4)rep(j,4)mx[i][j]=-inf;
	}
	int gmi(int x,int y){
		return min(mn[x][y],mn[y][x]);
	}
	int gma(int x,int y){
		return max(mx[x][y],mx[y][x]);
	}
	void upd(){
		cnt=0;
		rep(i,4)cnt+=to[i]->cnt;
		rep(i,4){
			int cs=0,mis=0,mas=0;
			int x=i,y=i;
			rep(_,4){
				int z=(y+1);
				if(z==4)z=0;

				cs+=to[y]->cnt;
				if(cs==cnt){
					mn[i][y]=mis+to[y]->gmi(x,y);
					mx[i][y]=mas+to[y]->gma(x,y);
				}else{
					mn[i][y]=inf;
					mx[i][y]=-inf;
				}

				mis+=to[y]->gmi(x,z);
				mas+=to[y]->gma(x,z);
				chmin(mis,inf);
				chmax(mas,-inf);
				x=y;y=z;
			}
		}
		rep(i,4)rep(j,4)chmin(mn[i][j],inf);
		rep(i,4)rep(j,4)chmax(mx[i][j],-inf);
	}
};
