template<typename T>
struct SegT {
private:
    int sz; vector<T> node;
    T init_c;
    function<T(T, T)> f;
public:
    SegT(vector<T> v, T _init_c, function<T(T, T)> _f) {
        init_c = _init_c; f = _f;
        int n = v.size();
        sz = 1;
        while (sz < n)sz *= 2;
        node.resize(2 * sz - 1, init_c);
        rep(i, n) {
            node[i + sz - 1] = v[i];
        }
        per(i, sz - 1) {
            node[i] = f(node[2 * i + 1], node[2 * i + 2]);
        }
    }
    SegT(int n, T _init_c, function<T(T, T)> _f) {
        init_c = _init_c; f = _f;
        sz = 1;
        while (sz < n)sz *= 2;
        node.resize(2 * sz - 1, init_c);
    }
    void update(int k, T a) {
        k += sz - 1;
        node[k] = a;
        while (k > 0) {
            k = (k - 1) / 2;
            node[k] = f(node[k * 2 + 1], node[k * 2 + 2]);
        }
    }
    T query(int a, int b, int k = 0, int l = 0, int r = -1) {
        if (r < 0)r = sz;
        if (r <= a || b <= l)return init_c;
        else if (a <= l && r <= b)return node[k];
        else {
            T vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
            T vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
            return f(vl, vr);
        }
    }
    //kf(x,node[y+sz-1])truey
    int searchloc(int le, T x, function<bool(T, T)> comp) {
        int  k = le + sz - 1;
        if (comp(x, node[k]))return le;
        x = f(x, node[k]);
        while (k > 0) {
            int mem = k;
            k = (k - 1) / 2;
            if (2 * k + 1 == mem) {
                if (comp(x, node[2 * k + 2])) {
                    k = 2 * k + 2; break;
                }
                x = f(x, node[2 * k + 2]);
            }
        }
        if (k == 0)return sz;
        while (k < sz - 1) {
            if (comp(x, node[2 * k + 1])) {
                k = 2 * k + 1;
            }
            else {
                x = f(x, node[2 * k + 1]);
                k = 2 * k + 2;
            }
        }
        return k - (sz - 1);
    }

};
