				} else {
					if (B[j] > A[i])
						net.addEdge(ia[i], ib[j], 1, 0);
				}
			} else {
				//directly attack
				net.addEdge(ia[i], ib[j], 1, -B[j]);
			}
		}
	}
	pair<int, int> ret = net.minCostFlow(vs, vt, true);
	if (ret.first == m)
		return -ret.second;
	return 0;
}

int main() {
	cin >> n >> m;
	for (int i = 0; i < n; ++i) {
		cin >> how[i] >> A[i];
	}
	for (int i = 0; i < m; ++i) {
		cin >> B[i];
	}
	int ans = notKillAll();
	if (m > n)
		ans = max(ans, killAll());
	cout << ans << endl;
	return 0;
}
