template<class T, class T2> inline int chkmax(T &x, const T2 &y) { return x < y ? x = y, 1 : 0; }
template<class T, class T2> inline int chkmin(T &x, const T2 &y) { return x > y ? x = y, 1 : 0; }
const int MAXN = (1 << 20);
const int MAXV = (int)1e6 + 42;

int64_t tr[4 * MAXN];
int64_t cnt[4 * MAXN];

void add(int pos, int64_t cnt, int64_t val, int l, int r, int idx)
{
	if(pos < l || pos > r) return;
	if(l == r)
	{
		tr[idx] += val * cnt;
		::cnt[idx] += cnt;
		return;
	}

	int mid = (l + r) >> 1;
	add(pos, cnt, val, l, mid, 2 * idx + 1);
	add(pos, cnt, val, mid + 1, r, 2 * idx + 2);

	tr[idx] = tr[2 * idx + 1] + tr[2 * idx + 2];
	::cnt[idx] = ::cnt[2 * idx + 1] + ::cnt[2 * idx + 2];
}

int64_t query(int64_t X, int l, int r, int idx)
{
	if(l == r) 
	{
		int64_t can = min(cnt[idx], X / l);
		return can;
	}

	int mid = (l + r) >> 1;
	if(tr[2 * idx + 1] <= X) return cnt[2 * idx + 1] + query(X - tr[2 * idx + 1], mid + 1, r, 2 * idx + 2);
	return query(X, l, mid, 2 * idx + 1);
}

int64_t T;

void add(int64_t cnt, int64_t v) { add(v, cnt, v, 1, MAXV, 0); }
void rem(int64_t cnt, int64_t v) { add(v, -cnt, v, 1, MAXV, 0); }
int64_t query(int64_t I) { return query(I, 1, MAXV, 0); }

