{
	add(x[u], t[u]);
	value[u] = query(T - 2ll * Len);

	for(auto e: adj[u])
		dfs1(e.first, Len + e.second);

	rem(x[u], t[u]);
}

int64_t dfs2(int u)
{
	int64_t ret = value[u];
	vector<int64_t> lis;
	for(auto e: adj[u])
