    int count;

    void assign(int value) {
        if (value == 0) {
            hashValue = 0;
            count = 0;
        } else {
            hashValue = value;
            count = 1;
        }
    }

    void assign(const State &l, const State &r) {
        hashValue = p4423[r.count] * l.hashValue + r.hashValue;
        count = l.count + r.count;
    }
