            states[IX(l, r)].assign(v);
        }
        return;
    }
    int m = l + r >> 1;
    if (p <= m) {
        update(l, m, p, v);
    }
    if (p > m) {
        update(m + 1, r, p, v);
    }
    states[IX(l, r)].assign(states[IX(l, m)], states[IX(m + 1, r)]);
}

