			ret += (sol2[1] - sol1[1]) + T(a[i][1] - a[i][0]) * (sol1[0]);
		}
		bit.clear();
	}
	{
		swap(a, b);
		int p = 0;
		vector<lint> vect;
		for (int j = 0; j < sz(b); j++) {
			vect.push_back(b[j][0]);
		}
		sort(all(vect));
		vect.resize(unique(all(vect)) - vect.begin());
		bit.thres = sz(vect);
		vector<int> comp(sz(b));
		for (int i = 0; i < sz(b); i++) {
			comp[i] = lower_bound(all(vect), b[i][0]) - vect.begin();
		}
		int lo;
		for (int i = 0; i < sz(a); i++) {
			while (p < sz(b) && b[p][1] <= a[i][1]) {
				bit.add(comp[p], {T(1), T(b[p][0])});
				p++;
			}
			ret += T(p) * T(a[i][0]);
			if (i == 0 || a[i - 1][1] - a[i - 1][0] != a[i][1] - a[i][0]) {
				lo = lower_bound(all(vect), a[i][1] - a[i][0]) - vect.begin();
			}
			auto sol1 = bit.query(lo - 1);
			auto sol2 = bit.total;
			ret += (sol2[1] - sol1[1]) + T(a[i][1] - a[i][0]) * (sol1[0]);
		}
		bit.clear();
	}
	return ret;
}

template <typename T> T solve(int s, int e, vector<int> &a) {
	if (s == e)
		return T(a[s]);
	vector<int> L, R;
	int m = (s + e) / 2;
	for (int i = m; i >= s; i--)
		L.push_back(a[i]);
	for (int i = m + 1; i <= e; i++)
		R.push_back(a[i]);
	T ret = merge<T>(L, R);
	return ret + solve<T>(s, m, a) + solve<T>(m + 1, e, a);
};

