using namespace std;
const int MAX = 200005;
long long h[MAX], d[MAX], sum[MAX], n;
struct node
{
	long long mx1, mx2, mx3;
} seg[4 * MAX];
node merge(node x, node y)
{
	node ans;
	ans.mx1 = max(x.mx1, y.mx1);
	ans.mx2 = max(x.mx2, y.mx2);
	ans.mx3 = max(x.mx3, y.mx3);
	ans.mx3 = max(ans.mx3, x.mx1 + y.mx2);
	return ans;
}
void build(int v = 1, int s = 0, int e = n)
{
	if (e - s < 2)
	{
		seg[v].mx1 = 2 * h[s] - sum[s];
		seg[v].mx2 = 2 * h[s] + sum[s];
		seg[v].mx3 = -1000000000000000LL;
		return;
	}
	int mid = (s + e) / 2;
	build(2 * v, s, mid);
	build(2 * v + 1, mid, e);
	seg[v] = merge(seg[2 * v], seg[2 * v + 1]);
}
node get(int l, int r, int v = 1, int s = 0, int e = n)
{
	if (l <= s && e <= r)
		return seg[v];
	if (e <= l || r <= s)
	{
		node x;
		x.mx1 = x.mx2 = x.mx3 = -1000000000000000LL;
		return x;
	}
	int mid = (s + e) / 2;
	node x = get(l, r, 2 * v, s, mid);
	node y = get(l, r, 2 * v + 1, mid, e);
	return merge(x, y);
}
int main()
{
	ios::sync_with_stdio(false);
	int n, q;
