	{
		h[i] = h[i - n];
		d[i] = d[i - n];
	}
	for (int i = 1; i <= 2 * n; i++)
		sum[i] = sum[i - 1] + d[i - 1];
	build();
	while (q--)
	{
		int l, r;
		cin >> l >> r;
		if (r < l)
			r += n;
		l--;
		int len = n - (r - l);
		cout << get(r, r + len).mx3 << "\n";
	}
	return 0;
}
