using namespace std;

using ll = long long;
using vi = vector<int>;
using pi = pair<int, int>;
using vpi = vector<pair<int, int>>;
using vvi = vector<vi>;

const int INFi = 2e9;
const ll INF = 2e18;

struct Line {
    ll k, b;

    ll get(ll x) { return k * x + b; }

    // returns the x-intercept of intersection "strictly" larger than T
    ll cross_after(Line &L, ll T) {
        assert(L.get(T) >= get(T));
        if (L.k >= k) return INF;
        if (L.b >= b) return (L.b - b + (k - L.k)) / (k - L.k);
        return (L.b - b + 1) / (k - L.k);
    }

    Line(ll k_ = 0, ll b_ = INF) : k(k_), b(b_) {}
};

struct KineticSegTree { // min kinetic segment tree
    struct Node {
        Line best;
        ll melt;
        ar<ll, 2> upd; // upd[0] - heaten, upd[1] - add x

        Node() : best(), melt(INF), upd({0, 0}) {}
    };

    ll T;
    int n;
    vector<Node> t;

    void apply(int v, ar<ll, 2> x) {
        t[v].upd[0] += x[0];
        t[v].upd[1] += x[1];
        t[v].best.b += x[0] * t[v].best.k + x[1];
        t[v].melt -= x[0];
    }

    bool IsBetter(Line &a, Line &b) {
        ll l = a.get(T), r = b.get(T);
        if (l != r)
            return l < r;
        return a.k < b.k;
    }

    void pull(int v) {
        Line A = t[v << 1].best;
        Line B = t[v << 1 | 1].best;
        t[v].melt = min(t[v << 1].melt, t[v << 1 | 1].melt);
        if (IsBetter(B, A)) swap(A, B);
        t[v].best = A;
        t[v].melt = min(t[v].melt, A.cross_after(B, T));
    }

    void init(vector<Line> &l, ll TT = 0) {
        n = l.size();
        T = TT;
        t.resize(n * 4);
        init(1, 0, n, l);
    }

    void init(int v, int l, int r, vector<Line> &lines) {
        if (l + 1 == r) {
            t[v].best = lines[l];
            return;
        }
        init(v << 1, l, (l + r) >> 1, lines);
        init(v << 1 | 1, (l + r) >> 1, r, lines);
        pull(v);
    }

    void push(int v) {
        if (!t[v].upd[0] && !t[v].upd[1]) return;
        apply(v << 1, t[v].upd);
        apply(v << 1 | 1, t[v].upd);
        t[v].upd[0] = t[v].upd[1] = 0;
    }

    void propagate(int v) {
        if (t[v].melt > 0) return;
        push(v);
        propagate(v << 1);
        propagate(v << 1 | 1);
        pull(v);
    }

    ll get(int v, int l, int r, int lq, int rq) {
        if (rq <= l || r <= lq) return INF;
        if (lq <= l && r <= rq) {
            return t[v].best.get(T);
        }
        push(v);
        return min(get(v << 1, l, (l + r) >> 1, lq, rq), get(v << 1 | 1, (l + r) >> 1, r, lq, rq));
    }

    ll get(int lq, int rq) {
        return get(1, 0, n, lq, rq);
    }

    void upd(int v, int l, int r, int lq, int rq, ar<ll, 2> &x) {
        if (rq <= l || r <= lq) return;
        if (lq <= l && r <= rq) {
            apply(v, x);
            propagate(v);
            return;
        }
        push(v);
        upd(v << 1, l, (l + r) >> 1, lq, rq, x);
        upd(v << 1 | 1, (l + r) >> 1, r, lq, rq, x);
        pull(v);
    }

    void upd(int lq, int rq, ar<ll, 2> x) {
        upd(1, 0, n, lq, rq, x);
    }

    void heaten(int lq, int rq, ll x) {
        upd(lq, rq, {x, 0});
    }

    void add(int lq, int rq, ll x) {
        upd(lq, rq, {0, x});
    }
};

void solve() {
    int n;
    cin >> n;
    int m;
    cin >> m;
    vi l(n), r(n);
    rep(i, n) {
        cin >> l[i] >> r[i];
