    vector<Line> a(m + 1, {0, 0});
    for (int i = 1; i <= m; ++i) {
        cin >> a[i].k;
        a[i].k *= -1;
    }
    KineticSegTree kst;
    kst.init(a, 0);
    vi ord(n);
    iota(all(ord), 0);
    sort(all(ord), [&](const int &i, const int &j) { return r[i] < r[j]; });
    int j = 0;
    for (int i = 1; i <= m; ++i) {
        ll ans = kst.get(0, i);
        kst.add(i, i + 1, ans);
        while (j < n && r[ord[j]] == i) {
            kst.heaten(l[ord[j]], r[ord[j]] + 1, 1);
            j++;
        }
    }
    cout << -kst.get(0, m + 1) << '\n';
}

