    int n, b, k1, k2;
    std::cin >> n >> b >> k1 >> k2;

    i64 sum = 0;
    std::vector<int> a(n);
    for (int i = 0; i < n; i++) {
        std::cin >> a[i];
        sum += a[i];
    }

    i64 ans = 0;

    std::sort(a.begin(), a.end(), std::greater());

    std::vector f(k1 + 1, std::vector<i64>(k2 + 1));
    int mid = 0;
    while (mid < n && a[mid] >= b) {
        mid++;
    }

    i64 res = 0;
    for (int i = 0; i <= k1; i++) {
        if (i > 0 && i <= mid) {
            res += a[i - 1] / 2;
        }
        int t = std::min(i, mid);
        i64 v = res;
        for (int j = 0; j <= k2; j++) {
            if (j > 0) {
                if (t + j <= mid) {
                    v += b;
                } else if (j <= mid) {
                    v += std::min(b, (a[t + j - mid - 1] + 1) / 2);
                }
            }
            f[i][j] = v;
        }
    }

    res = 0;
    for (int i = 0; i <= std::min(k2, n - mid); i++) {
        if (i > 0) {
            res += a[mid + i - 1];
        }
        i64 v = res;
        for (int j = 0; j <= std::min(k1, n - i - mid); j++) {
            if (j > 0) {
                v += a[mid + i + j - 1] / 2;
            }
            ans = std::max(ans, v + f[k1 - j][k2 - i]);
        }
    }

