const int INF = int(1e9) + 5;

struct state {
    int high = -INF, low = -INF;
    int previous = -1, a = 0;

    bool operator<(const state &other) const {
        if (high != other.high)
            return high < other.high;

        return low < other.low;
    }

    pair<state, bool> add(int from, int x) {
        if (x < low)
            return {state(), false};

        state sum = *this;
        sum.previous = from;
        sum.a = x;

        if (x > high)
            sum.high = x;
        else
            sum.low = x;

        return {sum, true};
    }
};

void run_case() {
    int N;
    cin >> N;
    vector<int> P(N);

    for (auto &p : P)
        cin >> p;

    vector<vector<state>> dp(N + 1);
    dp[0] = {state()};

    for (int i = 0; i < N; i++) {
        for (int x : {-P[i], P[i]})
            for (int j = 0; j < int(dp[i].size()); j++) {
                state s = dp[i][j];
                pair<state, bool> result = s.add(j, x);

                if (result.second)
                    dp[i + 1].push_back(result.first);
            }

        sort(dp[i + 1].begin(), dp[i + 1].end());
        vector<state> filtered;

        for (state s : dp[i + 1]) {
            bool bad = false;

            for (state f : filtered)
                if (f.low <= s.low && f.high <= s.high)
                    bad = true;

            if (!bad)
