    for (auto &x: s) pref[x]++;
    for (int i = 1; i <= s[n - 1]; ++i) pref[i] += pref[i - 1];
    ll ans = 0;
    array<int, 3> current = {-1, -1, 0};
    for (int x = 1; x <= s[n - 1]; ++x) {
