template<class E>
struct cactus{
	const vvc<E>&g;
	const int n;
	vvc<E> cs;
	vi vis,par,u;
	vc<E> come;
	vc<bool> done;
	void dfs(int v,int p,E co){
		assert(vis[v]==0);
		vis[v]=1;
		par[v]=p;
		come[v]=co;
		E gopar;
		for(auto e:g[v]){
			if(e==p){
				gopar=e;
			}else if(vis[e]==0){
				dfs(e,v,e);
			}else if(vis[e]==1){
				int x=v;
				vc<E> z{e};
				while(x!=e){
					assert(!done[x]);
					done[x]=true;
					z.pb(come[x]);
					x=par[x];
				}
				reverse(all(z));
				cs.pb(z);
			}
		}
		if(p!=-1&&!done[v]){
			done[v]=true;
			cs.pb({co,gopar});
		}
		vis[v]=2;
	}
	cactus(const vvc<E>&gg):g(gg),n(g.size()),vis(n),par(n),u(n),come(n),done(n){
		rep(i,n)if(!vis[i])
			dfs(i,-1,E());
	}
};
