  vector<num> fact(n + 1), inv_fact(n + 1);
  fact[0] = inv_fact[0] = 1;
  for (int i = 1; i <= n; ++i) {
    fact[i] = fact[i - 1] * i;
    inv_fact[i] = inv_fact[i - 1] / i;
  }
  auto binom = [&](int x, int y) {
    return fact[x] * inv_fact[y] * inv_fact[x - y];
  };
  num cur = 0;
