#define MAXN 300005

int n,m;

struct data {
	int l,r,size;
};

inline data operator + (const data &a,const data &b) {
	return (data){a.l==a.size ? a.size+b.l : a.l,b.r==b.size ? b.size+a.r : b.r,a.size+b.size};
}

char a[MAXN];

struct segnode {
	segnode *l,*r;
	data x;

	inline void update() {
		x=l->x+r->x;
	}
};

segnode seg[MAXN*2],*segtot=seg;

inline segnode * build(int l,int r) {
	segnode *ret=++segtot;
	if (l==r) {
		ret->x=(data){0,0,1};
	} else {
		int mid=(l+r)>>1;
		ret->l=build(l,mid);
		ret->r=build(mid+1,r);
		ret->update();
	}
	return ret;
}

inline data query(segnode *a,int l,int r,int L,int R) {
	if (l==L && r==R) return a->x;
	int mid=(L+R)>>1;
	if (r<=mid) {
		return query(a->l,l,r,L,mid);
	} else if (l>mid) {
		return query(a->r,l,r,mid+1,R);
	} else {
		return query(a->l,l,mid,L,mid)+query(a->r,mid+1,r,mid+1,R);
	}
}

inline void mod(segnode *a,int pos,data x,int L,int R) {
	if (L==R) {
		a->x=x;
	} else {
		int mid=(L+R)>>1;
		if (pos<=mid) {
			mod(a->l,pos,x,L,mid);
		} else {
			mod(a->r,pos,x,mid+1,R);
		}
		a->update();
	}
}

int ans;

segnode *root;

inline void modify(int pos,char x) {
	if (x!='.') x=0;
	if (x==a[pos]) return;
	a[pos]=x;
	if (x=='.') {
		data L,R;
		L=R=(data){0,0,0};
		if (pos>1) L=query(root,1,pos-1,1,n);
		if (pos<n) R=query(root,pos+1,n,1,n);
		ans-=std::max(L.r-1,0)+std::max(R.l-1,0);
		ans+=L.r+R.l;
		mod(root,pos,(data){1,1,1},1,n);
	} else {
		data L,R;
		L=R=(data){0,0,0};
		if (pos>1) L=query(root,1,pos-1,1,n);
		if (pos<n) R=query(root,pos+1,n,1,n);
		ans+=std::max(L.r-1,0)+std::max(R.l-1,0);
		ans-=L.r+R.l;
		mod(root,pos,(data){0,0,1},1,n);
	}
}

