int st[maxn << 2];
int lz1[maxn << 2];
int lz2[maxn << 2];

void pushdown(int p, int L, int R) {
    if (lz1[p]) {
        st[p] = (R - L + 1) - st[p];
        if (L < R) {
            if (~lz2[p << 1]) {
                lz1[p << 1] = 0;
                lz2[p << 1] ^= 1;
            }
            else {
                lz1[p << 1] ^= 1;
            }
            if (~lz2[p << 1 | 1]) {
                lz1[p << 1 | 1] = 0;
                lz2[p << 1 | 1] ^= 1;
            }
            else {
                lz1[p << 1 | 1] ^= 1;
            }
        }
        lz1[p] = 0;
    }
    if (~lz2[p]) {
        st[p] = lz2[p] * (R - L + 1);
        if (L < R) {
            lz2[p << 1] = lz2[p];
            lz1[p << 1] = 0;
            lz2[p << 1 | 1] = lz2[p];
            lz1[p << 1 | 1] = 0;
        }
        lz2[p] = -1;
    }
}

void upd1(int p, int l, int r, int L, int R) {
    pushdown(p, L, R);
    if (l > R || r < L) return;
    if (l <= L && r >= R) {
        lz1[p] = 1;
        pushdown(p, L, R);
        return;
    }
    upd1(p << 1, l, r, L, L + R >> 1);
    upd1(p << 1 | 1, l, r, (L + R >> 1) + 1, R);
    st[p] = st[p << 1] + st[p << 1 | 1];
}

void upd2(int p, int l, int r, int L, int R, int val) {
    pushdown(p, L, R);
    if (l > R || r < L) return;
    if (l <= L && r >= R) {
        lz2[p] = val;
        pushdown(p, L, R);
        return;
    }
    upd2(p << 1, l, r, L, L + R >> 1, val);
    upd2(p << 1 | 1, l, r, (L + R >> 1) + 1, R, val);
    st[p] = st[p << 1] + st[p << 1 | 1];
}

int query(int p, int L, int R) {
    pushdown(p, L, R);
    if (L == R) return L;
    pushdown(p << 1, L, L + R >> 1);
    pushdown(p << 1 | 1, (L + R >> 1) + 1, R);
    if (st[p << 1] < (L + R >> 1) - L + 1) return query(p << 1, L, L + R >> 1);
    return query(p << 1 | 1, (L + R >> 1) + 1, R);
}

void solve() {
    cin >> n;
