        if (op[i] == 1) {
            upd2(1, x, y, 0, sz(dc) - 1, 1);
        }
        else if (op[i] == 2) {
            upd2(1, x, y, 0, sz(dc) - 1, 0);
        }
        else if (op[i] == 3) {
