  Block(int l, int r) : L(l), R(r), ptr(0), increase(0), num_incrs(r - l) {}

  void IncrAll(int delta) { increase += delta; }

  void Rebuild() {
    VI order(SZ(num_incrs));
    iota(ALL(order), 0);
    sort(ALL(order), [&](int lhs, int rhs) {
