  int m = grp.size();

  dp[0][0] = 1;
  for (int i = 0; i < m; i++) {
    for (int j = 0; j <= n; j++) {
      if (!sieve[grp[i].first]) {
        dp[i + 1][j + 1] += dp[i][j] * ifact[grp[i].second - 1];
      }

