struct SegTree {
  int shift;
  vector<int> tree;
  vector<int> xs;
  void build() {
    make_unique(xs);
    shift = 1;
    while (shift < xs.size()) {
      shift *= 2;
    }
    tree.assign(2 * shift, 0);
  }
  int get_lower_bound(int coord) {
    return lower_bound(all(xs), coord) - xs.begin();
  }
  int get_sum(int l, int r) {
    l = get_lower_bound(l);
    r = get_lower_bound(r);
    l += shift;
    r += shift;
    int res = 0;
    while (l < r) {
      if (l & 1) {
        res += tree[l++];
        continue;
      }
      if (r & 1) {
        res += tree[--r];
        continue;
      }
      l /= 2;
      r /= 2;
    }
    return res;
  }
  void update(int x, int val) {
    int v = get_lower_bound(x);
    assert(xs[v] == x);
    v += shift;
    while (v) {
      tree[v] += val;
      v /= 2;
    }
  }
};

struct LargeTree {
  int shift;
  vector<SegTree> trees;
  LargeTree(int n) {
    shift = 1;
    while (shift < n) {
      shift *= 2;
    }
    trees.resize(2 * shift);
  }
  void build() {
    for (int i = 0; i < trees.size(); ++i) {
      trees[i].build();
    }
  }
  void raw_insert(int x, int y) {
    x += shift;
    while (x) {
      trees[x].xs.push_back(y);
      x /= 2;
    }
  }
  void insert(int x, int y) {
    x += shift;
    while (x) {
      trees[x].update(y, 1);
      x /= 2;
    }
  }
  int count(int lx, int rx, int ly, int ry) {
    lx += shift;
    rx += shift;
    int res = 0;
    while (lx < rx) {
      if (lx & 1) {
        res += trees[lx++].get_sum(ly, ry);
