namespace XX
{
    template<typename... Datas>
    struct UnionFind
    {
            struct Node:public Datas...
            {
                int _parent = -1;
                void operator+=(Node& other) { int dummy[] = {(Datas::operator+=(other), 0)...}; }
            };
            UnionFind(int size = 0) :_data(size, Node()) {}

            void resize(int size)
            {
                _data.resize(size, Node());
            }

            int size()
            {
                return _data.size();
            }

            Node& operator[](int idx) { return _data[(*this)(idx)]; }

            int operator()(int n)
            {
                if(_data[n]._parent < 0)
                    return n;
                else
                    return _data[n]._parent = (*this)(_data[n]._parent);
            }

            bool operator()(int a, int b)
            {
                int pa = (*this)(a), pb = (*this)(b);
                if(pa == pb)
                    return false;
                else
                {
                    if(_data[pa]._parent == _data[pb]._parent)
                        _data[pa]._parent--;
                    else if(_data[pa]._parent > _data[pb]._parent)
                        std::swap(pa, pb);

                    _data[pa] += _data[pb];
                    _data[pb]._parent = pa;
                    return true;
                }
            }

            std::vector<Node> _data;
    };
}








