struct Node {
    int term;
    int nxt[A];
    int link;
    int p, ps;

    Node() {
        ps = -1;
        p = -1;
        term = -1;
        fill(nxt, nxt + A, -1);
        link = -1;
    }
};

int ptr = 1;
Node trie[N];

int get_id(const string &s) {
    int cur_v = 0;
    for (char cc : s) {
        int c = cc - 'a';
        if (trie[cur_v].nxt[c] == -1) {
            assert(false);
        }
        cur_v = trie[cur_v].nxt[c];
    }

    return trie[cur_v].term;
}

void add(const string &s, int id) {
    int cur_v = 0;
    for (char cc : s) {
        int c = cc - 'a';
        if (trie[cur_v].nxt[c] == -1) {
            trie[cur_v].nxt[c] = ptr++;
            trie[ptr - 1].p = cur_v;
            trie[ptr - 1].ps = c;
        }
        cur_v = trie[cur_v].nxt[c];
    }

    trie[cur_v].term = id;
}

int link(int v);

int go(int v, int c) {
    if (trie[v].nxt[c] != -1) {
        return trie[v].nxt[c];
    }
    if (v == 0) {
        return 0;
    }

    return trie[v].nxt[c] = go(link(v), c);
}

int link(int v) {
    if (trie[v].link != -1) {
        return trie[v].link;
    }

    if (v == 0 || trie[v].p == 0) {
        return 0;
    }

    return trie[v].link = go(link(trie[v].p), trie[v].ps);
}

