void DFS(int x, int y){
    Num[x][y]=cnt;
    CC[cnt]++;
    if(!Num[x+1][y] && p[x+1][y] == '.')DFS(x+1,y);
    if(!Num[x-1][y] && p[x-1][y] == '.')DFS(x-1,y);
    if(!Num[x][y+1] && p[x][y+1] == '.')DFS(x,y+1);
    if(!Num[x][y-1] && p[x][y-1] == '.')DFS(x,y-1);
}
void Ins(int x, int y){
    int t = Num[x][y];
    if(!t)return;
    if(!C[t])SS += CC[t];
    C[t]++;
}
void Del(int x, int y){
    int t = Num[x][y];
    if(!t)return;
    C[t]--;
    if(!C[t])SS -= CC[t];
}
int Calc(int x, int y){
    return S[x+K-1][y+K-1] - S[x-1][y+K-1] - S[x+K-1][y-1] + S[x-1][y-1];
}
int main(){
    int i,j,k;
    scanf("%d%d",&n,&K);
    for(i=1;i<=n;i++){
