        SS = 0;
        for(j=1;j<=cnt;j++)C[j]=0;
        for(j=i-1;j<=i+K;j++){
            for(k=1;k<=K;k++){
                Ins(j,k);
            }
        }
        for(j=i;j<i+K;j++){
            Ins(j,K+1);
        }
        Res = max(Res,SS + K*K - Calc(i,1));
        for(j=2;j<=n-K+1;j++){
            for(k=i;k<i+K;k++){
                Del(k,j-2);
                Ins(k,j+K);
            }
            Del(i-1,j-1);
            Del(i+K,j-1);
            Ins(i-1,j+K-1);
            Ins(i+K,j+K-1);
