        std::cout << "No\n";
        return;
    }

    std::vector<int> L(n + 2, -1), R(n + 2, -1);
    for (int i = 0; i <= n; i++) {
        if (r[i] < r[i + 1]) {
            R[i] = r[i + 1];
            L[r[i + 1]] = i;
        }
    }

    std::vector<int> pl, pr;
    for (int i = 1; i <= n; i++) {
        if (R[i] == -1) {
            pl.push_back(i);
        }
        if (L[i] == -1) {
            pr.push_back(i);
        }
    }

    int k = pl.size();
    assert(pr.size() == k);
    int res = 0;
    for (int i = 0, j = 0; i < k; i++) {
        while (j < k && pl[i] >= pr[j]) {
            j++;
        }
        res = std::max(res, j - i);
    }
    for (int i = 0; i < k; i++) {
        R[pl[i]] = i + res < k ? pr[i + res] : n + 1;
        L[pr[i]] = i - res >= 0 ? pl[i - res] : 0;
    }

    std::vector<int> a(n + 1);
    int tot = 0;
