    for (int i = 1; i <= n + 1; i++) {
        f[i] = std::max(f[i], f[i - 1]);
    }

    for (int i = 1; i <= n; i++) {
        if (r[i] < f[i - 1]) {
            std::cout << "No\n";
            return;
        }
    }

    std::cout << "Yes\n";
    for (int i = 1; i <= n; i++) {
        std::cout << a[i] << " \n"[i == n];
    }
}

