#define all(x) x.begin(),x.end()
#define pb push_back
#define mp make_pair
#define sz(x) x.size()
using namespace std;

typedef long long ll;

enum colour {BLACK, RED};

template <class data>

class redblack
{
public:
    redblack *left, *right, *par;
    int subTreeSize;
    colour color;
    data compVal;

    data subTreeSum;
    data sumOfMax(int k)
    {
        if (subTreeSize <= k) return subTreeSum;
        ll ret = 0;
        if (right != NULL)
            {
                ret += right->sumOfMax(k);
                k -= right->subTreeSize;
            }
        if (k <= 0) return ret;
        ret += compVal * min(k, nodeSize());
        k -= nodeSize();
        if (k <= 0) return ret;
        ret += left->sumOfMax(k);
        return ret;
    }

    redblack* root()
    {
        if (par != NULL) return par->root();
        return this;
    }

    static redblack* create()
    {
        redblack *tree = new redblack();
        return tree;
    }

    int nodeSize()
    {
        return sz(values);
    }

    vector < data > getValues()
    {
        return values;
    }

    redblack find(data val)
    {
        if (isNil) return NULL;
        if (compVal == val) return this;
        redblack next = getNext(val);
        if (next == NULL) return NULL;
        return next.find(val);
    }

    redblack* treeMin()
    {
        if (left == NULL) return this;
        return treeMin(left);
    }

    redblack* treeMax()
    {
        if (right == NULL) return this;
        return treeMax(right);
    }

    redblack* succesor()
    {
        if (isNil) return NULL;
        if (right != NULL) return treeMin(right);
        redblack *pr = par, *ptr = this;        
        while(pr != NULL && ptr == pr->right)
            {
                ptr = pr;
                pr = ptr->par;
            }
        return pr;
    }

    redblack* predecessor()
    {
        if (isNil) return NULL;
        if (left != NULL) return treeMax(left);
        redblack *pr = par, *ptr = this;        
        while(pr != NULL && ptr == pr->left)
            {
                ptr = pr;
                pr = ptr->par;
            }
        return pr;
    }

    bool remove(data val)
    {
        if (isNil) return false;
        if (compVal == val)
            {
                if (sz(values))
                    {
                        values.pop_back();
                        subTreeSize--;
                        subTreeSum -= val;
                        return true;
                    }
                return false;
            }
        redblack *next = getNext(val);
        if (next == NULL) return false;
        bool deleted = next->remove(val);
        if (deleted)
            {
                subTreeSize--;
                subTreeSum -= val;
            }
        return deleted;
    }

    redblack* insert(data val)
      {
          if (isNil)
              {
                  isNil = false;
                  par = left = right = NULL;
                  compVal = val;
                  values.pb(val);
                  subTreeSize = 1;                  
                  subTreeSum = val;
                  color = BLACK;
                  return this;
              }
          if (compVal == val)
              {
                  values.pb(val);
                  subTreeSize++;
                  subTreeSum += val;
                  return this;
              }
          redblack *next = getNext(val);
          if (next == NULL)
              {
                  redblack *node = new redblack(this, val);
                  if (val > compVal) right = node; else left = node;
                  subTreeSize++;
                  subTreeSum += val;
                  return node;
              }
          redblack *inserted = next->insert(val);
          subTreeSize++;
          subTreeSum += val;
          if (par == NULL) inserted->insertBalance();
          return inserted;              
      }

    redblack* getNext(data val)
    {
        if (compVal > val) return left;
        return right;
    }

    void clear()
    {
        if (left != NULL) left->clear();
        if (right != NULL) right->clear();
        delete(this);
    }

private:
    bool isNil;
    vector < data > values;

    redblack()
    {
        par = left = right = NULL;
        isNil = true;
        color = RED;
        subTreeSize = 0;
        subTreeSum = 0;
    }

    redblack(redblack *parent, data val)
    {
        left = right = NULL;        
        par = parent;
        isNil = false;
        color = RED;
        compVal = val;
        values.pb(val);
        subTreeSize = 1;
        subTreeSum = val;
    }

    void rightRotate()
    {
        redblack *x = this, *y = x->left;
        if (x -> isNil || y == NULL || y->isNil) return;
        x->left = y->right;
        if (y->right != NULL) y->right->par = x;
        y->par = x->par;
        if (x->par != NULL)
            {
                if (x->par->left == x) x->par->left = y;
                else x->par->right = y;
            }
        y->right = x;
        x->par = y;
        y->subTreeSize = x->subTreeSize;
        x->subTreeSize = x->nodeSize() + ((x->left != NULL)?x->left->subTreeSize:0) + ((x->right != NULL)?x->right->subTreeSize:0);
        y->subTreeSum = x->subTreeSum;
        x->subTreeSum = x->nodeSize()*x->compVal + ((x->left != NULL)?x->left->subTreeSum:0) + ((x->right != NULL)?x->right->subTreeSum:0);
    }

    void leftRotate()
    {
        redblack *x = this, *y = x->right;
        if (x->isNil || y == NULL || y->isNil ) return;
        x->right = y->left;
        if (y->left != NULL) y->left->par = x;
        y->par = x->par;
        if (x->par != NULL)
            {
                if (x->par->left == x) x->par->left = y;
                else x->par->right = y;
            }
        y->left = x;
        x->par = y;
        y->subTreeSize = x->subTreeSize;
        x->subTreeSize = x->nodeSize() + ((x->left != NULL)?x->left->subTreeSize:0) + ((x->right != NULL)?x->right->subTreeSize:0);
        y->subTreeSum = x->subTreeSum;
        x->subTreeSum = x->nodeSize()*x->compVal + ((x->left != NULL)?x->left->subTreeSum:0) + ((x->right != NULL)?x->right->subTreeSum:0);
    }

    void insertBalance()
    {
        redblack *x = this, *y;

        while(x->par != NULL && x->par->color == RED)
            {
                if (x->par->par == NULL) return;
                if (x->par == x->par->par->left)
                    {
                        y = x->par->par->right;
                        if (y != NULL && y->color == RED)
                            {
                                x->par->color = y->color = BLACK;
                                x->par->par->color = RED;
                                x = x->par->par;
                            }
                        else
                            {
                                if (x == x->par->right)
                                    {
                                        x = x->par;
                                        x->leftRotate();
                                    }
                                x->par->color = BLACK;
                                x->par->par->color = RED;
                                x->par->par->rightRotate();
                            }
                    }
                else
                    {
                        y = x->par->par->left;    
                        if (y != NULL && y->color == RED)
                            {
                                x->par->color = y->color = BLACK;
                                x->par->par->color = RED;
