  auto init_b = b;
  vector<long long> da(n - 1);
  for (int i = 0; i < n - 1; i++) {
    da[i] = a[i + 1] - a[i];
  }
  vector<long long> db(m - 1);
  for (int i = 0; i < m - 1; i++) {
