        da[n - k + i - 1] += d;
      }
    } else {
      if (k == m) {
        b[0] += d;
        k -= 1;
      }
      fenw.modify(m - k - 1, d);
      fenw2.modify(m - k - 1, ((long long) -d) * (m - k - 2));
      ans += ((long long) d) * k * (k + 1LL) / 2;
    }
    long long res = ans;
    res += (a[0] + b[0]) * (n + m - 1);
    for (int i = 0; i < n - 1; i++) {
      int low = 0, high = m - 1;
      while (low < high) {
