['#include <bits/stdc++.h>

using namespace std;

const int N = 2e6 + 5;
const int M = 3e7 + 5;
int n, m, op[N], A[N], B[N], C[N];
vector<int> tr[N];

int dep[N], fa[N], sz[N], son[N];

void dfs1(int u, int pre) {
	dep[u] = dep[pre] + 1, fa[u] = pre, sz[u] = 1;
	for (int v : tr[u]) {
		if (v == pre) continue;
		dfs1(v, u);
		sz[u] += sz[v];
		if (sz[v] > sz[son[u]]) son[u] = v;
	}
}

int st[N], ed[N], id[N], top[N], cnt;

void dfs2(int u, int tp) {
	st[u] = ++cnt, id[cnt] = u, top[u] = tp;
	if (son[u]) dfs2(son[u], tp);
	for (int v : tr[u])
		if (v != fa[u] && v != son[u])
			dfs2(v, v);
	ed[u] = cnt;
}

struct Edge {
	int to, next;
} e[M];

int head[N], deg[N], cnte;

inline void addedge(int u, int v) {
	e[++cnte] = Edge{v, head[u]}, head[u] = cnte;
	++deg[v];
}

int prf_in[N], prf_out[N], tot;

#define ls(x) (x << 1)
#define rs(x) (x << 1 | 1)

int ver_in[N], ver_out[N];

void build(int x = 1, int l = 1, int r = n) {
	if (l == r) {
		ver_in[x] = ver_out[x] = id[l];
		return;
	}
	int mid = (l + r) >> 1;
	build(ls(x), l, mid);
	build(rs(x), mid + 1, r);
	ver_in[x] = ++tot, ver_out[x] = ++tot;
	addedge(ver_in[x], ver_in[ls(x)]);
	addedge(ver_in[x], ver_in[rs(x)]);
	addedge(ver_out[ls(x)], ver_out[x]);
	addedge(ver_out[rs(x)], ver_out[x]);
}

void addin(int L, int R, int s, int x = 1, int l = 1, int r = n) {
	if (L <= l && r <= R) {
		addedge(s, ver_in[x]);
		return;
	}
	int mid = (l + r) >> 1;
	if (L <= mid) addin(L, R, s, ls(x), l, mid);
	if (R > mid) addin(L, R, s, rs(x), mid + 1, r);
}

void addout(int L, int R, int t, int x = 1, int l = 1, int r = n) {
	if (L <= l && r <= R) {
		addedge(ver_out[x], t);
		return;
	}
	int mid = (l + r) >> 1;
	if (L <= mid) addout(L, R, t, ls(x), l, mid);
	if (R > mid) addout(L, R, t, rs(x), mid + 1, r);
}

int q[N], ans[N];

inline void toposort() {
	int l = 1, r = 0, res = 0;
	for (int i = 1; i <= tot; ++i)
		if (!deg[i]) q[++r] = i;
	while (l <= r) {
		int u = q[l++];
		if (u <= n) ans[u] = ++res;
		for (int i = head[u]; i; i = e[i].next) {
			int v = e[i].to;
			--deg[v];
			if (!deg[v]) q[++r] = v;
		}
	}
	if (r < tot) {
		cout << "-1\n";
	} else {
		for (int i = 1; i <= n; ++i) cout << ans[i] << ' ';
		cout << '\n';
	}
}

inline void treeaddanc(int x, int y, int op, int v) {
	while (top[x] != top[y]) {
		if (op == 1) addedge(v, prf_in[x]);
		else addedge(prf_out[x], v);
		x = fa[top[x]];
	}
	if (y == x) return;
	if (op == 1) addin(st[y] + 1, st[x], v);
	else addout(st[y] + 1, st[x], v);
}

inline void treeadd(int x, int y, int op, int v) {
	while (top[x] != top[y]) {
		if (dep[top[x]] >= dep[top[y]]) {
			if (op == 1) addedge(v, prf_in[x]);
			else addedge(prf_out[x], v);
			x = fa[top[x]];
		} else {
			if (op == 1) addedge(v, prf_in[y]);
			else addedge(prf_out[y], v);
			y = fa[top[y]];
		}
	}
	if (dep[x] < dep[y]) {
		if (op == 1) addin(st[x], st[y], v);
		else addout(st[x], st[y], v);
	} else {
		if (op == 1) addin(st[y], st[x], v);
		else addout(st[y], st[x], v);
	}
} 

int main() {
	ios::sync_with_stdio(0);
	cin.tie(0);
	cin >> n >> m;
	for (int i = 1; i < n; ++i) {
		int u, v;
		cin >> u >> v;
		tr[u].push_back(v);
		tr[v].push_back(u);
	}
	for (int i = 1; i <= m; ++i) cin >> op[i] >> A[i] >> B[i] >> C[i];
	dfs1(1, 0);
	dfs2(1, 1);
	tot = n;
	for (int i = 1; i <= n; ++i) {
		prf_in[i] = ++tot;
		prf_out[i] = ++tot;
	}
	for (int i = 1; i <= n; ++i) {
		addedge(prf_in[i], i);
		addedge(i, prf_out[i]);
		if (i != top[i]) {
			addedge(prf_in[i], prf_in[fa[i]]);
			addedge(prf_out[fa[i]], prf_out[i]);
		}
	}
	build();
	for (int i = 1; i <= m; ++i) {
		if (A[i] == B[i]) continue;
		if (A[i] != C[i]) {
			if (st[A[i]] >= st[C[i]] && ed[A[i]] <= ed[C[i]]) treeaddanc(A[i], C[i], op[i], C[i]);
			else treeadd(fa[C[i]], A[i], op[i], C[i]);
		}
		if (B[i] != C[i]) {
			if (st[B[i]] >= st[C[i]] && ed[B[i]] <= ed[C[i]]) treeaddanc(B[i], C[i], op[i], C[i]);
			else treeadd(fa[C[i]], B[i], op[i], C[i]);
		}
	}
	toposort();
	return 0;
}
