  sort(forc[v].begin(), forc[v].end());
  for (int j = 1; j < cnt; j++) {
    forc[v][j].second += forc[v][j - 1].second;
  }
  for (int i = 0; i < cnt; i++) {
    int u = perm[forc[v][i].first];
    in_forc[u].push_back(i);
  }
  vector <int> children;
  for (int i = 0; i < (int) g[v].size(); i++) {
    int u = g[v][i].first;
    if (alive[u]) {
      children.push_back(u);
    }
  }
  alive[v] = false;
  for (int i = 0; i < (int) children.size(); i++) {
    build(children[i]);
  }
}

