	Tree*pl, *pr;
	int l, r;

	void update() {
		for (int i = 0; i < 3; ++i) {
			for (int j = 0; j < 3; ++j) {
				int64 k = 0;
				for (int pi = 0; pi < 3; ++pi) {
					for (int pj = 0; pj < 3; ++pj)
						if (w[pi][pj]) {
							k += 1LL * pl->c[i][pi] * pr->c[pj][j];
						}
				}
				c[i][j] = k % MOD;
			}
		}
	}

	Tree(int l, int r) :
			l(l), r(r) {
		memset(c, 0, sizeof c);
		if (l + 1 == r) {
			for (int i = 0; i < 3; ++i) {
				c[i][i] = 1;
			}
			return;
		}
		pl = new Tree(l, l + r >> 1);
		pr = new Tree(l + r >> 1, r);
		update();
	}

	void change(int i, int x) {
		if (i < l || i >= r)
			return;
		if (l + 1 == r) {
			if (x == 0) {
				memset(c, 0, sizeof c);
				for (int j = 0; j < 3; ++j) {
					c[j][j] = 1;
				}
			} else {
				memset(c, 0, sizeof c);
				c[x - 1][x - 1] = 1;
			}
			return;
		}
		pl->change(i, x);
		pr->change(i, x);
		update();
	}
