['#include <bits/stdc++.h>

#define fo(i,a,b) for (int i = (a); i < (b); i++)
#define sz(v) int(v.size())

using namespace std;

typedef long long ll;
#define inf ll(1e18)

typedef pair<ll,int> dstwt;
dstwt dinf = dstwt(inf, -1);

vector<ll> pws = {1, 42, 1764, 74088, 3111696, 130691232, 5489031744, 230539333248, 9682651996416, 406671383849472, 17080198121677824, 717368321110468608};

dstwt recompd(dstwt a) {
    while (a.first < 0) {
        int pi = a.second;
        ll nd = pws[pi+1] - pws[pi];
        a.first += nd;
        a.second++;
    }
    return a;
}

struct stb {
    // upds
    // 1) a[i] = d,np
    // 2) a[i].d -= x
    // and: go to ranges of constant np where d < 0

    struct nd {
        dstwt mnd, smnd; 
        ll add;
        dstwt set;
        bool lset;

        nd(dstwt a = dinf) {
            mnd = a;
            smnd = dinf;
            add = 0; 
            lset = true;
            set = a;
        }
    };

    nd rt[400005];

    void apply(int i, int op, dstwt x) {
        nd &r = rt[i];
        if (op == 1) {
            ll y = x.first;
            // add y
            r.mnd.first -= y;
            if (r.smnd != dinf) r.smnd.first -= y;
            if (r.lset) r.set.first -= y;
            else r.add += y;
        } else if (op == 2) {
            // set to x
            r.mnd = x;
            r.smnd = dinf;
            r.add = 0;
            r.lset = true;
            r.set = x;
        }
    }

    void merge(int i) {
        nd &p = rt[i];
        const nd &l = rt[2*i+1]; 
        const nd &r = rt[2*i+2];

        p.mnd = min(l.mnd, r.mnd);
        p.smnd = min(l.mnd == p.mnd ? l.smnd : l.mnd, r.mnd == p.mnd ? r.smnd : r.mnd);
        p.add = 0;
        p.lset = false;
    }

    void push(int i) {
        const nd &r = rt[i];
        if (r.lset) {
            apply(2*i+1, 2, r.set);
            apply(2*i+2, 2, r.set);
        } else if (r.add) {
            apply(2*i+1, 1, dstwt(r.add,0));
            apply(2*i+2, 1, dstwt(r.add,0));
        }
        merge(i);
    }

    void upd(int i, int nl, int nr, int ql, int qr, int op, dstwt x) {
        if (nl == ql && nr == qr) apply(i, op, x);
        else {
            push(i);
            int nmd = (nl+nr)/2;
            if (ql < nmd) upd(2*i+1, nl, nmd, ql, min(qr, nmd), op, x);
            if (qr > nmd) upd(2*i+2, nmd, nr, max(ql, nmd), qr, op, x);
            merge(i);
        }
    }

    void recomp(int i, int nl, int nr) {
        if (rt[i].mnd.first >= 0) return;
        else if (rt[i].smnd == dinf) {
            rt[i].mnd = recompd(rt[i].mnd);
            rt[i].smnd = dinf;
            rt[i].add = 0;
            rt[i].lset = true;
            rt[i].set = rt[i].mnd;
        } else {
            push(i);
            int nmd = (nl+nr)/2;
            recomp(2*i+1, nl, nmd);
            recomp(2*i+2, nmd, nr);
            merge(i);
        }
    }

    ll qu(int i, int nl, int nr, int qi) {
        if (rt[i].lset) return pws[rt[i].set.second] - rt[i].set.first;
        push(i);
        int nmd = (nl+nr)/2;
        if (qi < nmd) return qu(2*i+1, nl, nmd, qi);
        else return qu(2*i+2, nmd, nr, qi);
    }

    void set(int n, int ql, int qr, ll x) {
        dstwt ux = recompd({1-x,0});
        upd(0, 0, n, ql, qr, 2, ux);
    }

    void add(int n, int ql, int qr, ll x) {
        dstwt ux(x,-1);
        while (true) {
            upd(0, 0, n, ql, qr, 1, ux);
            recomp(0, 0, n);
            if (rt[0].mnd.first > 0) break;
            assert(rt[0].mnd.first == 0);
        }
    }

    void init(int i, int nl, int nr, ll *v) {
        if (nl+1 == nr) {
            rt[i] = recompd({1-v[nl],0});
        } else {
            int nmd = (nl + nr) / 2;
            init(2*i+1, nl, nmd, v);
            init(2*i+2, nmd, nr, v);
            merge(i);
        }
    }
};

stb s;
ll a[200005];
int n, q;

int main() {
    scanf("%d %d", &n, &q);
    fo(i,0,n) scanf("%lld", a+i);
    s.init(0, 0, n, a);
    fo(_,0,q) {
        int qt;
        scanf("%d", &qt);
        if (qt == 1) {
