}

}

LL read() {
    using namespace _buff;
    LL ret = 0;
    bool pos = true;
    char c = getc();
    for (; (c < '0' || c > '9') && c != '-'; c = getc()) {
        assert(~c);
    }
    if (c == '-') {
        pos = false;
        c = getc();
    }
    for (; c >= '0' && c <= '9'; c = getc()) {
        ret = (ret << 3) + (ret << 1) + (c ^ 48);
    }
    return pos ? ret : -ret;
}

const size_t N = 105;
const int MOD = 998244353;

int qpow(int base, int e) {
    int ret = 1;
    for (; e; e >>= 1) {
        if (e & 1) {
            ret = (LL) ret * base % MOD;
        }
        base = (LL) base * base % MOD;
    }
    return ret;
}

void iadd(int &dp, const int &val) {
    dp += val;
    if (dp >= MOD) {
        dp -= MOD;
    }
}

void ladd(int &dp, const LL &val) {
    dp = (dp + val) % MOD;
}

int inv[N];

void prep_inv() {
    inv[0] = 1;
    for (unsigned i = 1; i < N; ++i) {
        inv[i] = (LL) inv[i - 1] * qpow(i, MOD - 2) % MOD;
    }
}

int comb(int l, int n) {
    if (!n) return 1;
    int ret = (LL) inv[n] * qpow(l, MOD - 1 - n) % MOD;
    for (int i = 0; i < n; ++i) {
        ret = (LL) ret * (l + i) % MOD;
    }
    return ret;
}

int n, l[N], r[N];
int f[N][N][N];

int main() {
    prep_inv();
    n = read();
    vector<int> vc = {-1};
    for (int i = 0; i < n; ++i) {
        l[i] = read();
        r[i] = read() + 1;
        vc.emplace_back(l[i]);
        vc.emplace_back(r[i]);
    }
    reverse(l, l + n);
    reverse(r, r + n);
    sort(vc.begin(), vc.end());
    vc.erase(unique(vc.begin(), vc.end()), vc.end());
    int segs = (int) vc.size() - 1;
    f[0][0][0] = 1;
    for (int i = 0; i < n; ++i) {
        int sum = 0;
        for (int id = 0; id < segs; ++id) {
            int lb = vc[id], rb = vc[id + 1];
            if (l[i] <= lb && rb <= r[i]) {
                int prob = min(rb, r[i]) - max(lb, l[i]);
                f[i + 1][id][1] = (LL) sum * prob % MOD;
                for (int j = 1; j < n; ++j) {
                    f[i + 1][id][j + 1] = (LL) f[i][id][j] * prob % MOD;
                }
            }
            for (int j = 0; j < n; ++j) {
                if (f[i][id][j]) {
                    if (id) {
                        ladd(sum, (LL) f[i][id][j] * comb(rb - lb, j));
                    } else {
                        ladd(sum, f[i][id][j]);
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < segs; ++i) {
        int len = vc[i + 1] - vc[i];
        for (int j = 1; j <= n; ++j) {
            ladd(ans, (LL) f[n][i][j] * comb(len, j));
        }
    }
    for (int i = 0; i < n; ++i) {
        ans = (LL) ans * qpow(r[i] - l[i], MOD - 2) % MOD;
    }
    printf("%d\n", ans);
    return 0;
}
