		if (d[i].b == 0)
			return;

		if (i >= MAXN) {
			d[i].lo += d[i].b;
			d[i].hi += d[i].b;
			d[i].z = d[i].lo == 0 ? i - MAXN + 1 : -1;
			d[i].b = 0;
			return;
		}

		if (!(d[i].lo + d[i].b <= 0 && 0 <= d[i].hi + d[i].b)) {
			d[i].lo += d[i].b;
			d[i].hi += d[i].b;
			d[2*i].b += d[i].b;
			d[2*i+1].b += d[i].b;
			d[i].b = 0;
			d[i].z = -1;
			return;
		}

		d[2*i].b += d[i].b;
		d[2*i+1].b += d[i].b;
		push(2*i);
		push(2*i+1);
		d[i] = d[2*i] + d[2*i+1];
	}

