struct Matrix {
    int v[6][6];
    void clear() {
        memset(v, 0, sizeof(v));
    }
    Matrix() {
        clear();
    }
} e, f, a;

Matrix operator*(const Matrix &a, const Matrix &b) {
    Matrix c;
    for (int i = 0; i < 6; ++ i)
        for (int j = 0; j < 6; ++ j)
            for (int k = 0; k < 6; ++ k)
                c.v[i][j] = (c.v[i][j] + (ll)a.v[i][k] * b.v[k][j] % n) % n;
    return c;
}

Matrix mat_pow(const Matrix &a, ll b) {
    Matrix res = e, tmp = a;
    while (b) {
        if (b & 1) res = res * tmp;
        b >>= 1;
        tmp = tmp * tmp;
    }
    return res;
}

