int cntSpec[N];
ll sumVal[N];
int h[N];
int minH[N];
int root;
int ed[N][3];
bool isBridge[N];
int myComp[N];
ll val[N];
map<pii, ll> dp;
int deg[N];
ll ans[N];
int q[2 * N];
int topQ;

int getOther(int id, int v) {
	return ed[id][0] ^ ed[id][1] ^ v;
}

void dfs1(int v) {
//	eprintf("dfs1 %d %d\n", v, h[v]);
	minH[v] = h[v];
	for (int id : g[v]) {
		int u = getOther(id, v);
		if (h[u] != -1) {
			if (h[u] != h[v] - 1)
				minH[v] = min(minH[v], h[u]);
			continue;
		}
		h[u] = h[v] + 1;
		dfs1(u);
		cntSpec[v] += cntSpec[u];
//		sumVal[v] += sumVal[u];
		minH[v] = min(minH[v], minH[u]);
		if (minH[u] > h[v] && cntSpec[u] > 0) {
			isBridge[id] = 1;
		}
	}
}
void dfsComp(int v) {
	myComp[v] = k;
	val[k] += sumVal[v];
	for (int id : g[v]) {
		if (isBridge[id]) continue;
		int u = getOther(id, v);
		if (myComp[u] == -1)
			dfsComp(u);
	}
}

void solve(int v) {
	if (deg[v] == 1) {
		pii u = mp(-1, -1);
		ll sum = val[v];
		for (pii e : G[v]) {
			int w = e.first;
			if (dp.count(mp(v, w))) {
				sum += dp[mp(v, w)];
			} else {
				u = e;
			}
		}
		if (u.first != -1) {
			int w = u.first;
			sum = max(0LL, sum - u.second);
			dp[mp(w, v)] = sum;
			deg[w]--;
			if (deg[w] <= 1) q[topQ++] = w;
		}
	} else if (deg[v] == 0) {
		ll sum = val[v];
		for (pii e : G[v]) {
			int w = e.first;
			sum += dp[mp(v, w)];
		}
		for (pii e : G[v]) {
			int w = e.first;
			if (dp.count(mp(w, v))) continue;
			dp[mp(w, v)] = max(0LL, sum - dp[mp(v, w)] - e.second);
			deg[w]--;
			if (deg[w] <= 1) q[topQ++] = w;
		}
	} else throw;
}

int main()
{
